// Cart management
let cart = JSON.parse(localStorage.getItem('cart')) || [];

// Function to display products on the homepage
function displayProducts() {
    const productContainer = document.getElementById('product-list');
    productContainer.innerHTML = '';

    products.forEach(product => {
        const productCard = `
            <div class="col-md-3 mb-4 text-black">
                <div class="product-card">
                    <img src="${product.image}" alt="${product.name}">
                    <p>Price: ${product.price}</p>
                    <button class="btn btn-primary" onclick="addToCart(${product.id})">Add to Cart</button>
                </div>
            </div>
        `;
        productContainer.innerHTML += productCard;
    });
}

// Add product to cart and redirect to cart page
function addToCart(productId) {
    const product = products.find(p => p.id === productId);
    if (product) {
        // Check if the product is already in the cart
        const existingProduct = cart.find(item => item.id === productId);
        if (existingProduct) {
            existingProduct.quantity += 1;
        } else {
            product.quantity = 1;
            cart.push(product);
        }
        localStorage.setItem('cart', JSON.stringify(cart));
        alert(`${product.name} has been added to the cart!`);
        window.location.href = "cart.html";  // Redirect to cart page after adding the product
    }
}

// Display cart items on the cart page
function displayCart() {
    const cartContainer = document.getElementById('cart-items');
    cartContainer.innerHTML = '';

    if (cart.length === 0) {
        cartContainer.innerHTML = '<p>Your cart is empty.</p>';
        return;
    }
    

    cart.forEach(item => {
        const cartItem = `
            <div class="row mb-3 text-black">
            <span
                <div class="col-6">${item.name}</div>
                <div class="col-2">${item.price}</div>
                <div class="col-2">
                    <input type="number" class="form-control" value="${item.quantity}" min="1" onchange="updateQuantity(${item.id}, this.value)">
                </div>
                <div class="col-2">
                    <button class="btn btn-danger" onclick="removeFromCart(${item.id})">Remove</button>
                </div>
            </div>
        `;
        cartContainer.innerHTML += cartItem;
    });

    // Update total price
    const totalPrice = cart.reduce((sum, item) => sum + item.price * item.quantity, 0);
    document.getElementById('total-price').innerText = `{totalPrice.toFixed(2)}`;
}

// Update quantity of a product in the cart
function updateQuantity(productId, quantity) {
    const product = cart.find(item => item.id === productId);
    if (product) {
        product.quantity = parseInt(quantity);
        localStorage.setItem('cart', JSON.stringify(cart));
        displayCart();
    }
}

// Remove product from cart
function removeFromCart(productId) {
    cart = cart.filter(item => item.id !== productId);
    localStorage.setItem('cart', JSON.stringify(cart));
    displayCart();
}

// Call displayProducts on the home page
if (document.getElementById('product-list')) {
    displayProducts();
}

// Call displayCart on the cart page
if (document.getElementById('cart-items')) {
    displayCart();
}
